import { Select } from 'antd';
import 'antd/dist/antd.css';
//import axios from 'axios';
import { useEffect, useState } from 'react';
import { InputNumber } from 'antd';
import { Space } from 'antd';

const { Option } = Select;

const indicators = ['RSI', 'MACD', 'KD', '三大法人買超'];
const indicatorsNum = {
  RSI: ['1', '2', '3'],
  MACD: ['4', '5', '6'],
  KD: ['7', '8', '9'],
  三大法人買超: ['10', '11', '12'],
};

const Indicators = ({ setIndicator, setRate, setClickCheck }) => {

    const onChangeIndicator = (value) => {
        console.log(`selected ${value}`);
        setIndicator(indicators[value]);
        setRate(indicatorsNum[value][0]);
        setClickCheck(false);
    }

    const onChangeIndNum = (value) => {
        console.log(`selected ${value}`);
        setRate(indicatorsNum(value));
        setClickCheck(false);
    }
      
    const onSearch = (val) => {
        console.log('search:', val);
    }
      
    return(
        <div>
            <Space className='indicators'>
                <Select defaultValue={indicators[0]} 
                        style={{ width: 120 }} 
                        onChange={onChangeIndicator}
                        filterOption={(input, option) =>
                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }>
                    {indicators.map(indicator => (
                        <Option key={indicator}>{indicator}</Option>
                    ))}
                </Select>
                <Select style={{ width: 120 }} value={rate} onChange={onChangeIndNum}>
                    {indicatorsNum.map(rate => (
                    <Option key={rate}>{rate}</Option>
                    ))}
                </Select>
            </Space>
        </div>
    );

}



export default Indicators
