import axios from 'axios';
import { useState, useEffect } from 'react';
import CreateStrategyEMA from './CreateStrategyEMA';
import CreateStrategyRSI from './CreateStrategyRSI';
import CreateStrategyMACD from './CreateStrategyMACD';
import CreateStrategyKD from './CreateStrategyKD';


const CreateStrategy = ({ company, indicator, num1, num2, stopProfit, stopLoss }) => {

  const [strategy, setStrategy] = useState('')
  const [str, setStr] = useState('策略未創建成功')

  useEffect(() => {
    if(indicator == 'RSI'){
      axios.post('http://127.0.0.1:8000/api/create/rsi', {         //某種function，傳入各種數值進入策略的資料庫中
        "Company_id": company,
        "Length": num1,
        "Threshold": num2,
        "Profit": stopProfit, 
        "Loss": stopLoss
      })
      .then((res) => { 
        setStrategy(res.data)
      })
      .catch((error) => { console.log(error) })
    }
    
      
  },[])
  if ({indicator} == 'RSI'){
    CreateStrategyRSI({ company, num1, num2, stopProfit, stopLoss, setStrategy })
  }
  else if ({indicator} == 'MACD'){
    CreateStrategyMACD({ company, num1, num2, stopProfit, stopLoss, setStrategy })
  }
  else if ({indicator} == 'KD'){
    CreateStrategyKD({ company, num1, num2, stopProfit, stopLoss, setStrategy })
  }
  else{
    CreateStrategyEMA({ company, num1, num2, stopProfit, stopLoss, setStrategy })
  }

  if ({strategy} == 'RSI strategy successfully created.' || 
        {strategy} == 'MACD strategy successfully created.' ||
        {strategy} == 'KD strategy successfully created.' || 
        {strategy} == 'EMA strategy successfully created.'){
          setStr = '策略已成功創建'
        }

  return <>
  <h3>{str}</h3>
  </>
}

export default CreateStrategy
