import 'antd/dist/antd.css';
//import axios from 'axios';
import { useEffect, useState } from 'react';
import { InputNumber } from 'antd';
import { Space } from 'antd';

const IndicatorNum = ({ indicator, setNum1, setNum2, setClickCheck }) => {

      const [str1, setStr1] = useState('設定長度(2~25): ');
      const [str2, setStr2] = useState('設定閾值(10~40):');
    
      const decide = (indicator) => {
        if (indicator == 'RSI'){
          setStr1('設定長度(2~25): ');
          setStr2('設定閾值(10~40): ');
        }
        else if (indicator == 'KD'){
          setStr1('設定長度(2~30): ');
          setStr2('設定閾值(10~40): ');
        }
      }

      decide({indicator});
      const onChange1 = (value) => {
        console.log(`selected ${value}`);
        setNum1(value);
        setClickCheck(false);
      }

      const onChange2 = (value) => {
        console.log(`selected ${value}`);
        setNum2(value);
        setClickCheck(false);
      }
      
      return(
        <div>
            <Space className='num'>
                <h3>{str1}</h3>
                <InputNumber 
                min={0} 
                max={100} 
                defaultValue={5} 
                onChange={onChange1} />
                <h3>{str2}</h3>
                <InputNumber 
                min={0} 
                max={100} 
                defaultValue={30} 
                onChange={onChange2} />
            </Space>
        </div>
      );

}

export default IndicatorNum
